mapper.select:
select count(1) from au_auth
where status != 2

  and name LIKE concat(concat('%',#{pageVO.data.name,jdbcType=VARCHAR}),'%')


  and type = #{pageVO.data.type,jdbcType=INTEGER}


  and status = #{pageVO.data.status,jdbcType=INTEGER}

select a.id, a.sys_id, a.name, a.status, a.type, a.url, a.auth_key, a.sort_no, a.parent_id, a.clazz, a.target, a.description, a.create_user_id, a.create_time, a.update_user_id, a.update_time, b.name AS parentName from au_auth a left join au_auth b on a.parent_id = b.id where a.status != 2
and a.name LIKE concat(concat('%',#{pageVO.data.name,jdbcType=VARCHAR}),'%')
and a.type = #{pageVO.data.type,jdbcType=INTEGER}
and a.status = #{pageVO.data.status,jdbcType=INTEGER}
ORDER BY a.create_time DESC LIMIT #{pageVO.dbIndex,jdbcType=INTEGER},#{pageVO.dbNumber,jdbcType=INTEGER}
