mapper.update:
update au_user
set password = #{password,jdbcType=VARCHAR}
where id = #{userId,jdbcType=INTEGER}

mapper.select:
select count(1) from au_user
where status != 2 and account = #{account,jdbcType=VARCHAR}

  and id != #{id,jdbcType=INTEGER}

select count(1) from au_user
where status != 2 and phone = #{phone,jdbcType=VARCHAR}

  and id != #{id,jdbcType=INTEGER}
